catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    (function executeRule(current, gForm, gSNC) {
        // Get the selected value from the user_group field
        var selectedGroupId = gForm.getValue('user_group');

        // If no value is selected in user_group, clear group_manager
        if (!selectedGroupId) {
            gForm.clearValue('group_manager');
            return;
        }

        // Perform a GlideAjax call to fetch the manager value
        var ga = new GlideAjax('GetManager'); // Use the Script Include name
        ga.addParam('sysparm_name', 'getManager'); // Specify the function to call
        ga.addParam('sysparm_reference_id', selectedGroupId);
		ga.getXML(function(response) {
            var managerId = response.responseXML.documentElement.getAttribute("answer"); // The returned manager sys_id
            if (managerId) {
                gForm.setValue('group_manager', managerId); // Set group_manager with the fetched value
            }
        }); // Pass the sys_id of user_group

    })(current, gForm, gSNC);

}
