function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    // Check if the new priority value is P1 (Critical)
    if (newValue === '1') { // '1' corresponds to P1 (Critical) in the Priority field
        // Add an error message to the form, displayed in red
        var res = confirm('Please explain detail your issues and provide full steps to reproduce the issue');
        if (res == true) {
			g_form.addErrorMessage('Please explain detail your issues and provide full steps to reproduce the issue');
            return true;
        } else {
            g_form.setValue('priority', oldValue);
        }

    } else {
        // Clear any previously shown error messages when the priority is not P1
        g_form.clearMessages();
    }
}
