sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {

    },
    isDatePast: function(strDate) {
        var rightnow = new GlideDateTime();
        var testday = new GlideDateTime(strDate);
        if (testday.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isToday: function(strDate) {
        var rightnow = new GlideDateTime();
        var testday = new GlideDateTime(strDate);
        var today = rightnow.getLocalDate();
        var istoday = testday.getLocalDate();
        if (today.compare(istoday) == 0) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
